import 'package:json_annotation/json_annotation.dart'; part 'user_data.g.dart';@JsonSerializable( ignoreUnannotated: false)class UserData {  @JsonKey(name: 'message')  List<Message> message;  UserData({required this.message});   factory UserData.fromJson(Map<String, dynamic> json) => _$UserDataFromJson(json);   Map<String, dynamic> toJson() => _$UserDataToJson(this);}@JsonSerializable(nullable: false, ignoreUnannotated: false)class Message {  @JsonKey(name: 'name')  String name;  @JsonKey(name: 'full_name')  String fullName;  Message({required this.name, required this.fullName});   factory Message.fromJson(Map<String, dynamic> json) => _$MessageFromJson(json);   Map<String, dynamic> toJson() => _$MessageToJson(this);}