import 'package:injectable/injectable.dart';import '../datasources/restaurant_api.dart';import '../models/restaurant/restaurant.dart';@Injectable()class RestaurantRepository {  final RestaurantAPI api;  RestaurantRepository(this.api);  Future<Restaurant> fetchRestaurantItem({required String restaurant,required String deliverType,}) async {    final response = await api.fetchRestaurantData(  restaurant, deliverType);    return response;  }}